	public static class GamePacket
	{
		public static byte[] ConvertToHexString(string value)
		{
			var output = new System.Collections.Generic.List<byte>();
			value = value.Replace(" ", "");

			if (value.Length % 2 != 0)
			{
				throw new System.InvalidOperationException("Hex string size is not even.");
			}

			for (int i = 0; i < value.Length; i += 2)
			{
				output.Add((byte.Parse(value.Substring(i, 2), System.Globalization.NumberStyles.HexNumber)));
			}

			return output.ToArray();
		}

		public static void Game_VersionCheck(Client c, int characterID)
		{
			using (OutPacket plew = new OutPacket(ServerOpcode.GAMELOG))
			{
				plew.WriteInt(0);
				plew.WriteInt(0);
				plew.WriteInt(891); //characterID
				plew.WriteInt(ServerConstants.CLIENT_VERSION);
				plew.WriteInt(ServerConstants.UDP_PORT);
				plew.WriteInt(Randomizer.Next()); // Time Login
				plew.WriteByte(byte.Parse(c.Title.Split('.')[0])); //IP
				plew.WriteByte(byte.Parse(c.Title.Split('.')[1])); //IP
				plew.WriteByte(byte.Parse(c.Title.Split('.')[2])); //IP
				plew.WriteByte(byte.Parse(c.Title.Split('.')[3])); //IP
				plew.WriteLong(c.SessionID); // Key
				c.SendCrypto(plew);
			}
		}



		public static void Game_ServerTime(Client c)
		{
			using (OutPacket plew = new OutPacket(ServerOpcode.SERVER_TIME))
			{
				plew.WriteInt(0); // length + CRC
				plew.WriteInt(0);
				plew.WriteInt(DateTime.Now.Year);
				plew.WriteInt(DateTime.Now.Month);
				plew.WriteInt(0);
				plew.WriteInt(DateTime.Now.Day);
				plew.WriteInt(DateTime.Now.Hour);
				plew.WriteInt(DateTime.Now.Minute);
				c.Send(plew);
			}
		}



		public static void getNotice(Client c, byte type, string message)
		{
			using (OutPacket plew = new OutPacket(ServerOpcode.NOTICE))
			{
				plew.WriteInt(0); // length + CRC
				plew.WriteInt(0);
				plew.WriteByte(type);
				plew.WriteString(message, 63);
				//plew.WriteHexString("00 00 00 00 00 00 00");
				c.Send(plew);
			}
		}


		public static void Game_ServerStatus(Client c)
		{
			using (OutPacket plew = new OutPacket(ServerOpcode.SERVER_STATUS))
			{
				plew.WriteInt(0);//
				plew.WriteInt(0);// length + crc


				plew.WriteInt(0);
				plew.WriteInt(0);
				plew.WriteInt(0);
				c.Send(plew);

			}
		}


		public static void FW_MANAGER(Client c)
		{
			using (OutPacket plew = new OutPacket(ServerOpcode.FW_MANAGER))
			{
				//Opcode = 5F
				//05 01 5F 01 14 00 78 02 00 00 00 00 00 00 00 00 FF FF FF FF
				plew.WriteInt(0);
				plew.WriteInt(0); // length + crc


				plew.WriteInt(0);
				plew.WriteHexString("FF FF FF FF");
				c.Send(plew);

			}
		}


